def test_parse_nucleotide():
    pth = os.path.join(MISC_PATH, 'raxml_nucleotide.output')
    p = raxml.Parser('DNA')
    result = p.parse(open(pth).read())

def test_parse_aminoacid():
    pth = os.path.join(MISC_PATH, 'raxml_aminoacid.output')
    p = raxml.Parser('protein')
    result = p.parse(open(pth).read())

